pragma solidity ^0.4.11;

import 'zeppelin-solidity/contracts/ownership/Ownable.sol';
import "./Entity.sol";

/**
 * @title EntityFactory
 * @dev Standard contract that can create orders on the chain when called
 */
contract EntityFactory is Ownable {
	event EntityCreated(address entity, address owner, string rootHash, string offChainLocation);

	function createEntity (string rootHash, string offChainLocation) public returns (Entity entity) {
		Entity ent = new Entity(rootHash, offChainLocation);
		//TODO instead of using Zeppelin Ownable - implement our own so that we can set the owner in the constructor
		//as this external call to change ownership costs more gas
		ent.transferOwnership(msg.sender);
		EntityCreated(ent, ent.owner(), rootHash, offChainLocation);
		return ent;
	}
}
