pragma solidity ^0.4.11;

import 'zeppelin-solidity/contracts/ownership/Ownable.sol';
import "./OrderAgreement.sol";

/**
 * @title OrderAgreementFactory
 * @dev Standard contract that can create orders on the chain when called
 */
contract OrderAgreementFactory is Ownable {
	event OrderCreated(address order, address owner, string rootHash, string offChainLocation);

	function createOrderAgreement (string rootHash, string offChainLocation) public returns (OrderAgreement orderAgreement) {
		OrderAgreement oa = new OrderAgreement(rootHash, offChainLocation);
		//TODO instead of using Zeppelin Ownable - implement our own so that we can set the owner in the constructor
		//as this external call to change ownership costs more gas
		oa.transferOwnership(msg.sender);
		OrderCreated(oa, oa.owner(), rootHash, offChainLocation);
		return oa;
	}
}
